class SvgIconsController < ApplicationController
  ALLOWED_SIZES = [24, 28]
  skip_before_action :set_time_zone, :set_page_cache_indicator

  caches_page :show

  def show
    @size = params[:size].to_i
    head(:not_found) && return unless @size.in?(ALLOWED_SIZES)
    @icons = icon_sets[params[:icon_set].try(:to_sym)]
    respond_to do |format|
      format.svg
    end
  end

